<script type="text/javascript">        function delete_link(id_revco){            if(confirm('anda yakin akan menghapus data ini ?')){                goto('frmrevco/del/'+id_revco);            }    }    function ceknol(obj){            if(obj.value<=9){                return '0'+obj.value;            }else{                return obj.value;            }        }    function autonorev(){            var vnolab = document.frm1.nolab;            var vnourut = document.frm1.nourut;            var vjenis = document.frm1.jenisrev;            var vhrevco = document.frm1.hnorevco;            var vrevco =document.frm1.norevco;            if(vnolab.selectedIndex == 0){                vnolab.value = '';            }            if(vjenis.selectedIndex ==0){                vjenis.value = '';            }            vrevco.value=ceknol(vnolab) + vnourut.value + vjenis.value;              vhrevco.value = vrevco.value;        }            function autoupnorev(){            var vnolab = document.frm2.uplab;            var vnourut = document.frm2.upnourut;            var vjenis = document.frm2.upjenisrev;            var vhrevco = document.frm2.hupnorevco;            var vrevco =document.frm2.upnorevco;            if(vnolab.selectedIndex == 0){                vnolab.value = '';            }            if(vjenis.selectedIndex ==0){                vjenis.value = '';            }            vrevco.value=ceknol(vnolab) + vnourut.value + vjenis.value;              vhrevco.value = vrevco.value;        }            function combobox_select(obj, value) {		for (var i=0; i < obj[0].options.length; i++) {			if (obj[0].options[i].value == value) {				obj[0].options[i].selected = true;			}		}	}	    function edit_link_action(id_revco) {  				var objTR = $('#id_revco_'+id_revco).get(0);		// alert(objTR);                $('#dialog input[name="hupnorevco"]').val( $.trim( objTR.children[1].innerHTML ));		$('#dialog input[name="upnorevco"]').val( $.trim( objTR.children[1].innerHTML ));		combobox_select($('#dialog select[name="uplab"]').val( $('#id_revco_'+id_revco+ ' input[name="Lab"]').val()));                $('#dialog input[name="upnourut"]').val( $.trim( objTR.children[3].innerHTML ) );		combobox_select($('#dialog select[name="upjenisrev"]').val( $('#id_revco_'+id_revco+ ' input[name="jenisrev"]').val()));		$('#dialog input[name="upkapasitas"]').val( $.trim( objTR.children[5].innerHTML ) );	} 		$(function() {		$( "#dialog" ).dialog({			autoOpen: false,			width: 900,		});		document.dialog_click = function(id_revco) {						$( "#dialog" ).dialog( "open" );						edit_link_action(id_revco);		}	});		function addrevco_submit() {		var vnolab = $ID('nolab');		var vnourut = $ID('nourut');		var vjenis = $ID('jenisrev');		var vkapasitas = $ID('kapasitas');		if(vnolab.value=='' || vnolab.selectedIndex==0 ){			alert("No Laboratorium harus di pilih.");			vnolab.focus();			return false;		}		else if(vnourut.value=='' || !(vnourut.value.length==2) ){			alert("No Urut tidak boleh kosong dan harus 2 digit.");			vnourut.focus();			return false;		}		else if(vjenis.value=='' || vjenis.selectedIndex==0 ){			alert("Jenis Revco harus di pilih.");			vjenis.focus();			return false;		}                else if(vkapasitas.value=='' || vkapasitas.selectedIndex==0 ){			alert("kapasitas rak harus di isi.");			kapasitas.focus();			return false;		}				else {		$ID('frm1').submit();		// return true;		}                       <?php if (isset($status_addrevco) && $status_addrevco == 1) { ?>	jQuery(document).ready(function(){		alert('Penambahan data berhasil');	});	<?php } ?>              }       	function uprevco_submit() {                var vnolab = $ID('uplab');		var vnourut = $ID('upnourut');		var vjenis = $ID('upjenisrev');		var vkapasitas = $ID('upkapasitas');				if(vnolab.value=='' || vnolab.selectedIndex==0 ){			alert("Laboratorium harus di pilih.");			vnolab.focus();			return false;		}		else if(vnourut.value=='' || !(vnourut.value.length==2) ){			alert("No Urut tidak boleh kosong dan harus 2 digit.");			vnourut.focus();			return false;		}		else if(vjenis.value=='' || vjenis.selectedIndex==0 ){			alert("Jenis Revco harus di pilih.");			vjenis.focus();			return false;		}else if(vkapasitas.value=='' || vkapasitas.selectedIndex==0 ){			alert("kapasitas rak harus di isi.");			vkapasitas.focus();			return false;		}				else {		$ID('frm2').submit();		// return true;		}	}	                <?php if (isset($status_uprevco) && $status_uprevco == 1) { ?>	jQuery(document).ready(function(){		alert('Update Data Berhasil');	});	<?php } ?>		</script><div class="page-header">	<div class="pull-left">		<h4><i class="icon-table"></i> Data Master Revco</h4>	</div>	<div class="pull-right">		<ul class="bread">			<li><a href="<?php echo $application_path; ?>/home">Home</a><span class="divider">/</span></li>			<li><a href="#">Master</a><span class="divider">/</span></li>						<li class='active'>Revco</li>		</ul>	</div></div><div class="container-fluid" id="content-area">	<div class="row-fluid">		<div class="span12">			<div class="box">				<div class="box-head box-tabs">					<ul class="tabs">						<li class="active">							<a href="#add" data-toggle="tab">								<i class="icon-flag"></i>								Tambah Revco							</a>						</li>						<li>							<a href="#list" data-toggle="tab">								List Revco							</a>						</li>					</ul>				</div>			<div class="box-body box-body-nopadding">					<div class="tab-content">						<div class="tab-pane active" id="add">							<div class="row-fluid">								<div class="span12">                                                                </div>							</div>					<div class="row-fluid">					<form method="POST" id="frm1" name="frm1" class='form-horizontal'>						<input type="hidden" name="status_addrevco" value="0">						<div class="control-group